// junand 19.09.2017

//----------------------------------------------------------------

const DEFAULT_RECIPE_NAME = "unknown";
const DEFAULT_BREW_START = "unknown";
const DEFAULT_STEP_DESCIPTION = "unknown";
const DEFAULT_TEMP = 1;
const DEFAULT_HEATING = "control";
const DEFAULT_STIRRING = "off";
const DEFAULT_COOLING = "off";
const DEFAULT_HYSTERESIS = 1;
const DEFAULT_DURATION = 1;
const DEFAULT_HEATER_ON = false;
const DEFAULT_STIRRER_ON = false;
const DEFAULT_COOLER_ON = false;

var recipeName = context.get ( "recipeName" );
var brewStart = context.get ( "brewStart" );
var stepDescription = context.get ( "stepDescription" );
var targetTemp = context.get ( "targetTemp" ) || DEFAULT_TEMP;
var brewTemp = context.get ( "brewTemp" ) || DEFAULT_TEMP;
var heatingMode = context.get ( "heating" ) || DEFAULT_HEATING;
var stirringMode = context.get ( "stirring" ) || DEFAULT_STIRRING;
var coolingMode = context.get ( "cooling" ) || DEFAULT_COOLING;
var hysteresis = context.get ( "hysteresis" ) || DEFAULT_HYSTERESIS;
var periodLength = context.get ( "periodLength" ) || DEFAULT_DURATION;
var alarm = context.get ( "alarm" );

const PERIOD_INCREMENT = global.get( "timeScale" ); // normal -> 1, accelareted -> 30; <- accelerate time in config flow
const PERIOD_ZERO = { min: 0, sec: 0 };
var periodCount = context.get ( "periodCount" ) || PERIOD_ZERO;
var heaterOn = context.get ( "heaterOn" ) || DEFAULT_HEATER_ON;
var stirrerOn = context.get ( "stirrerOn" ) || DEFAULT_STIRRER_ON;
var coolerOn = context.get ( "coolerOn" ) || DEFAULT_COOLER_ON;

const STATE_IDLE = "idle";
const STATE_START = "start";
const STATE_STARTING = "starting";
const STATE_PERIOD_STARTED = "started";
const STATE_PERIOD_FINISHED = "finished";

const STATE_COLOR = [];
STATE_COLOR [STATE_IDLE] = "grey";
STATE_COLOR [STATE_START] = "white";
STATE_COLOR [STATE_STARTING] = "yellow";
STATE_COLOR [STATE_PERIOD_STARTED] = "green";
STATE_COLOR [STATE_PERIOD_FINISHED] = "black";

const LCD_ALARM_MSG_DURATION = 5; //sec
var lcdAlarmPeriod = context.get ( "alarmPeriod" );
var lcdAlarmMsg = context.get ( "alarmMsg" );
var lcdSparge = context.get ( "lcdSparge" ) || false;

var spargeTemp = context.get ( "spargeTemp" );

//----------------------------------------------------------------

var state = context.get ( "state" ) || STATE_IDLE;

if ( state == STATE_IDLE ) {
    node.status ( { fill: STATE_COLOR [state], shape: "dot", text: state } );
}

//----------------------------------------------------------------

function incPeriodCount ( cnt ) {
    cnt.sec += PERIOD_INCREMENT;
    if ( cnt.sec > 59 ) {
        cnt.sec = 0;
        cnt.min++;
    }
    return cnt;
}

//----------------------------------------------------------------

function createHeaterMessage ( onOff ) { return { payload: onOff }; }
function createStirrerMessage ( onOff ) { return { payload: onOff }; }
function createCoolerMessage ( onOff ) { return { payload: onOff }; }

function createStepMessage () { return { topic: "next", payload: "x" }; }

function createPeriodeMessage ( count ) { return { payload: count.min + ":" + ("0" + count.sec).slice ( -2 ) }; }

function createAlarmMessage ( notification ) { return { payload: notification }; }

function createAudioMessage ( audio ) { return { payload: audio };
}

function createChartMessage ( temp, target ) { 
    if ( target ) {
        return [
            { topic: "value", payload: temp },
            { topic: "target", payload: target }
        ];
    }
    else {
        return { topic: "value", payload: temp };
    }
}

function createInfluxMessage () {
    return {
        measurement: "°C",
        payload: [
            // fields
            {
                state: state,
                step: stepDescription,
                brewTemp: brewTemp,
                target: targetTemp,
                heater: heaterOn ? "on" : "off",
                stirrer: stirrerOn ? "on" : "off",
                cooler: coolerOn ? "on" : "off",
                counterMin: periodCount.min,
                counterSec: periodCount.sec,
            },
            // tags
            {
                recipe: recipeName,
                brewStart: brewStart
            }
        ]
    };
}

function centerLine ( c1, s, c2 ) {

    len = 18;

    if ( s.length > len ) {
        s = s.slice ( 0, len );
    }

    var l1 = ((len - s.length - 0.5) / 2).toFixed ( 0 );
    var l2 = len - s.length - l1;

    return c1 + " ".repeat ( l1 ) + s + " ".repeat ( l2 ) + c2;

}

function createLcdMessage ( step ) {

    //node.warn ( "[LCD] topic=" + msg.topic + " state=" + state + " recipe=" + recipeName + " heaterOn=" + heaterOn + " stirrerOn=" + stirrerOn + " step=" + step + " stepDescription=" + stepDescription + " brewTemp=" + brewTemp + " targetTemp=" + targetTemp );

    if ( lcdSparge ) {
        return {
            payload: {
                msgs: [
                    { msg: "Nachguss" },
                    { msg: "                   " },
                    { msg: "t1 - blau: " + (spargeTemp && spargeTemp.temperature1.toFixed ( 1 ) + "ßC" || "--") },
                    { msg: "t2 - gelb: " + (spargeTemp && spargeTemp.temperature2.toFixed ( 1 ) + "ßC" || "--") },
                ]
            }
        };
    }
    else {
        if ( state == STATE_IDLE ) {
            return {
                payload: {
                    msgs: [
                        { msg: "####################" },
                        { msg: "#   PiBrew Ready   #" },
                        { msg: centerLine ( "#", recipeName || "", "#" ) },
                        { msg: "####################" },
                    ]
                }
            };
        }
        else if ( lcdAlarmMsg ) {
            var s1, s2;
            if ( lcdAlarmMsg.length > 20 ) {
                s1 = lcdAlarmMsg.slice ( 0, 20 );
                s2 = lcdAlarmMsg.slice ( 20, 40 );
            }
            else {
                s1 = lcdAlarmMsg;
                s2 = " ";
            }
            return {
                payload: {
                    msgs: [
                        { msg: "!!!!!!!!!!!!!!!!!!!!" },
                        { msg: s1 },
                        { msg: s2 },
                        { msg: periodCount.min + ":" + ("0" + periodCount.sec).slice ( -2 ) + " min", center: true }
                    ]
                }
            };
        }
        else {
            return {
                payload: {
                    msgs: [
                        { msg: recipeName || DEFAULT_RECIPE_NAME },
                        { msg: step ? step : (stepDescription || DEFAULT_STEP_DESCIPTION).slice ( 0, 20 ) },
                        { msg: centerLine ( heaterOn ? "+" : coolerOn ? "~" : " ", brewTemp.toFixed ( 1 ) + "ßC -> " + targetTemp.toFixed ( 1 ) + "ßC", stirrerOn ? "*" : coolerOn ? "~" : " " ) },
                        { msg: periodCount.min + ":" + ("0" + periodCount.sec).slice ( -2 ) + " min", center: true }
                    ]
                }
            };
        }
    }
}

//----------------------------------------------------------------

function setState ( newState ) {
    state = newState;
    context.set ( "state", state );
    node.status ( { fill: STATE_COLOR [state], shape: "dot", text: state } );
}

function setHeaterOn ( value ) {
    heaterOn = value;
    context.set ( "heaterOn", heaterOn );
}

function setStirrerOn ( value ) {
    stirrerOn = value;
    context.set ( "stirrerOn", stirrerOn );
}

function setCoolerOn ( value ) {
    coolerOn = value;
    context.set ( "coolerOn", coolerOn );
}

//----------------------------------------------------------------

if ( false && msg.topic != "tick" && msg.topic != "FF2D57A41605" ) {
    node.warn ( "[EVENT] topic=" + msg.topic + " state=" + state + " min=" + periodCount.min + " sec=" + periodCount.sec );
}

if ( msg.topic == "finished" ) {
    setState ( STATE_IDLE );
    setHeaterOn ( false );
    setStirrerOn ( false );
    setCoolerOn ( false );
    return [createLcdMessage (), createHeaterMessage ( "OFF" ), createStirrerMessage ( "OFF" ), createCoolerMessage ( "OFF" )];
}
else if ( msg.topic == "recipe" ) {
    recipeName = msg.recipeName;
    context.set ( "recipeName", recipeName );
    setState ( STATE_IDLE );
    setHeaterOn ( false );
    setStirrerOn ( false );
    setCoolerOn ( false );
    return [createLcdMessage (), createHeaterMessage ( "OFF" ), createStirrerMessage ( "OFF" ), createCoolerMessage ( "OFF" )];
}
else if ( msg.topic == "stop" ) {
    setState ( STATE_IDLE );
    setHeaterOn ( false );
    setStirrerOn ( false );
    setCoolerOn ( false );
    return [createLcdMessage (), createHeaterMessage ( "OFF" ), createStirrerMessage ( "OFF" ), createCoolerMessage ( "OFF" )];
}
else if ( msg.topic == "start" ) {

    recipeName = msg.recipeName || recipeName || DEFAULT_RECIPE_NAME;
    brewStart = msg.brewStart || brewStart || DEFAULT_BREW_START;
    stepDescription = msg.step || step || DEFAULT_STEP_DESCIPTION;

    var t = msg.temperature || DEFAULT_TEMP; 
    if ( t > 0 && t < 105 ) targetTemp = t;

    heatingMode = msg.heating || heatingMode || DEFAULT_HEATING;
    stirringMode = msg.stirring || stirringMode || DEFAULT_STIRRING;
    coolingMode = msg.cooling || coolingMode || DEFAULT_COOLING;
    if ( heatingMode != "off" ) coolingMode = DEFAULT_COOLING;

    var h = msg.hysteresis || DEFAULT_HYSTERESIS;
    if ( h > 0 && h < 10 ) hysteresis = h;
    var p = msg.duration || DEFAULT_DURATION;
    if ( p === "wait" || p > 0 ) periodLength = p;

    //node.warn ( "[START] mode=" + heatingMode + " stirrer=" + stirringMode + " cooler=" + coolingMode + " t=" + targetTemp + " h=" + hysteresis + " p=" + periodLength );

    context.set ( "recipeName", recipeName );
    context.set ( "brewStart", brewStart );
    context.set ( "stepDescription", stepDescription );
    context.set ( "targetTemp", targetTemp );
    context.set ( "heating", heatingMode );
    context.set ( "stirring", stirringMode );
    context.set ( "cooling", coolingMode );
    context.set ( "hysteresis", hysteresis );
    context.set ( "periodLength", periodLength );
    context.set ( "alarm", msg.alarm );

    context.set ( "periodCount", PERIOD_ZERO );

    setState ( STATE_START );

    return [createLcdMessage (), null, null, null, null, createPeriodeMessage ( PERIOD_ZERO )];

}
else if ( msg.topic == "hysteresis" ) {
    context.set ( "hysteresis", hysteresis );
}
else if ( msg.topic == "tick" ) { // every sec
    // check lcd alarm
    if ( lcdAlarmPeriod && lcdAlarmPeriod > 0 ) {
        lcdAlarmPeriod--;
        context.set ( "alarmPeriod", lcdAlarmPeriod );
    }
    else {
        lcdAlarmPeriod = null;
        lcdAlarmMsg = null;
        context.set ( "alarmPeriod", null );
        context.set ( "alarmMsg", null );
    }
    // check for period length
    if ( state == STATE_PERIOD_STARTED ) {
        context.set ( "periodCount", incPeriodCount ( periodCount ) );
        var alarmMsg = null;
        var audioMsg = null;
        if ( alarm ) {
            for ( var i = 0; i < alarm.length; i++ ) {
                var alarmtime = null;
                if ( typeof ( alarm [i] ) === "number" ) alarmtime = alarm [i];
                else alarmtime = alarm [i].time;
                if ( periodCount.min == alarmtime ) {
                    if ( periodCount.sec === 0 || alarmtime === 0 && periodCount.sec === PERIOD_INCREMENT ) { // exception for 00:00, 1 sec later
                        lcdAlarmMsg = alarm [i].text;
                        context.set ( "alarmMsg", lcdAlarmMsg );
                        lcdAlarmPeriod = LCD_ALARM_MSG_DURATION;
                        context.set ( "alarmPeriod", lcdAlarmPeriod );
                        alarmMsg = createAlarmMessage ( lcdAlarmMsg );
                        audioMsg = createAudioMessage ( "ring" );
                        break;
                    }
                }
            }
        }
        if ( periodLength == "wait" ) {} // do nothing, only tick and maybe ring
        else {
            if ( periodCount.min >= periodLength ) {
                setState ( STATE_PERIOD_FINISHED );
                context.set ( "targetTemp", null );
                if ( !audioMsg ) audioMsg = createAudioMessage ( "tweet2x" );
                return [createLcdMessage (), null, null, null, null, createPeriodeMessage ( periodCount ), null, createStepMessage (), alarmMsg, audioMsg]; 
            }
        }

        //node.warn ( "[TICK] p=" + periodLength + " min=" + periodCount.min + " sec=" + periodCount.sec + " state=" + state );

        // tick
        return [createLcdMessage (), null, null, null, null, createPeriodeMessage ( periodCount ), null, null, alarmMsg, audioMsg];
    }
}
else if ( msg.topic == "nodes@home/control/ssr/brewery/socket1" ) {
    setHeaterOn ( msg.payload == "ON" ? true : false );
    return [createLcdMessage (), createHeaterMessage ( msg.payload )];
}
else if ( msg.topic == "nodes@home/control/ssr/brewery/socket2" ) {
    setStirrerOn ( msg.payload == "ON" ? true : false );
    return [createLcdMessage (), null, createStirrerMessage ( msg.payload )];
}
else if ( msg.topic == "nodes@home/control/ssr/brewery/valve" ) {
    setCoolerOn ( msg.payload == "ON" ? true : false );
    return [createLcdMessage (), null, null, createCoolerMessage ( msg.payload )];
}
else if ( msg.topic == "lcd" ) {
    lcdSparge = !lcdSparge;
    context.set ( "lcdSparge", lcdSparge )
    return [createLcdMessage ()];
}
else if ( msg.topic == "nodes@home/sensor/ds18b20/brewery/value/temperature" ) {
    context.set ( "spargeTemp", msg.payload );
    return [createLcdMessage ()];
}
else {
    // temperature received
    if ( state == STATE_IDLE ) {} // do nothing
    else {
        brewTemp = msg.payload;
        context.set ( "brewTemp", brewTemp );
        if ( state == STATE_PERIOD_FINISHED ) {
            return [createLcdMessage (), null, null, null, createChartMessage ( brewTemp ), null, createInfluxMessage ()];
        }
        else {

            // first temp event after start step            
            if ( state == STATE_START ) {
                setState ( STATE_STARTING );
            }

            var heaterMsg = null;
            var stirrerMsg = null;
            var coolerMsg = null;
            var alarmMsg = null;
            var audioMsg = null;
            var newHeaterOn = null;
            var newStirrerOn = null;
            var newCoolerOn = null;

            if ( coolingMode == "off" ) {
                if ( brewTemp >= targetTemp ) {
                    if ( heaterOn && heatingMode != "full" ) newHeaterOn = false;
                    if ( stirrerOn && stirringMode != "full" ) newStirrerOn = false;
                    if ( state == STATE_STARTING ) {
                        setState ( STATE_PERIOD_STARTED );
                        audioMsg = createAudioMessage ( "tweet2x" );
                    }
                }
            }
            else {
                if ( brewTemp <= targetTemp ) {
                    if ( coolerOn && coolingMode != "full" ) newCoolerOn = false;
                    if ( state == STATE_STARTING ) {
                        setState ( STATE_PERIOD_STARTED );
                        audioMsg = createAudioMessage ( "tweet2x" );
                    }
                }
            }

            if ( heatingMode == "off" ) {
                if ( heaterOn ) newHeaterOn = false;
            }
            else if ( heatingMode == "one_time" ) {
                if ( state == STATE_STARTING ) {
                    if ( !heaterOn ) newHeaterOn = true;
                }
            }
            else if ( heatingMode == "control" ) {
                if ( brewTemp <= targetTemp - hysteresis ) {
                    if ( !heaterOn ) newHeaterOn = true;
                }
            }
            else if ( heatingMode == "full" ) {
                if ( !heaterOn ) newHeaterOn = true;
            }

            if ( newHeaterOn !== null ) {
                //node.warn ( "switch heater from " + heaterOn + " to " + newHeaterOn );                    
                setHeaterOn ( newHeaterOn );
                heaterMsg = createHeaterMessage ( newHeaterOn ? "ON" : "OFF" );
                if ( !audioMsg ) audioMsg = createAudioMessage ( "tweet" );
            }

            if ( stirringMode == "off" ) {
                if ( stirrerOn ) newStirrerOn = false;
            }
            else if ( stirringMode == "control" ) {
                if ( brewTemp <= targetTemp - hysteresis ) {
                    if ( !stirrerOn ) newStirrerOn = true;
                }
            }
            else if ( stirringMode == "full" ) {
                if ( !stirrerOn ) newStirrerOn = true;
            }

            if ( newStirrerOn !== null ) {
                //node.warn ( "switch stirrer from " + stirrerOn + " to " + newStirrerOn );                    
                setStirrerOn ( newStirrerOn );
                stirrerMsg = createStirrerMessage ( newStirrerOn ? "ON" : "OFF" );
            }

            if ( coolingMode == "off" ) {
                if ( coolerOn ) newCoolerOn = false;
            }
            else if ( coolingMode == "one_time" ) {
                if ( state == STATE_STARTING ) {
                    if ( !coolerOn ) newCoolerOn = true;
                }
            }
            else if ( coolingMode == "full" ) {
                if ( !coolerOn ) newCoolerOn = true;
            }

            if ( newCoolerOn !== null ) {
                //node.warn ( "switch cooler from " + coolerOn + " to " + newCoolerOn );                    
                setCoolerOn ( newCoolerOn );
                coolerMsg = createCoolerMessage ( newCoolerOn ? "ON" : "OFF" );
            }

            //node.warn ( "[TEMP] heater=" + heaterOn + " newHeater=" + newHeaterOn + " mode=" + heatingMode + " p=" + periodLength + " min=" + periodCount.min + " sec=" + periodCount.sec + " state=" + state );
            //node.warn ( "[TEMP] stirrer=" + stirrerOn + " newStirrer=" + newStirrerOn + " mode=" + stirringMode + " p=" + periodLength + " min=" + periodCount.min + " sec=" + periodCount.sec + " state=" + state );
            //node.warn ( "[TEMP] cooler=" + coolerOn + " newCooler=" + newCoolerOn + " mode=" + coolingMode + " p=" + periodLength + " min=" + periodCount.min + " sec=" + periodCount.sec + " state=" + state );

            // on every temp, send target and data fields
            return [createLcdMessage (), heaterMsg, stirrerMsg, coolerMsg, createChartMessage ( brewTemp, targetTemp ), null, createInfluxMessage (), null, alarmMsg, audioMsg];

        }
    }
}

