//node.warn ( "payload=" + msg.payload + " topic=" + msg.topic );

function getStopMessages ( topic, recipeName ) {

    return [
        { 
            topic: topic||"stop",
            recipeName: recipeName,
            payload: "x"
        },
        [
            { id: 0, payload: false },
            { id: 1, payload: false },
            { id: 2, payload: false },
            { id: 3, payload: false },
            { id: 4, payload: false },
            { id: 5, payload: false },
            { id: 6, payload: false },
            { id: 7, payload: false },
            { id: 8, payload: false },
            { id: 9, payload: false },
            { id: 10, payload: false },
            { id: 11, payload: false },
            { id: 12, payload: false }
        ]
    ];

}

function createDateString ( d ) {

    var datestring = 
        ("0" + d.getDate ()).slice ( -2 ) + "-" + 
        ("0" + (d.getMonth () + 1)).slice ( -2 ) + "-" +
        d.getFullYear () + " " + 
        ("0" + d.getHours ()).slice ( -2 ) + ":" + 
        ("0" + d.getMinutes ()).slice ( -2 );

    return datestring;        
}

function getStepMessages ( i ) {

    var recipe = context.get ( "recipe" );
    if ( i === 0 ) { 
        recipe.brewStart = createDateString ( new Date () );
        context.set ( "recipe", recipe );
    }
    if ( i < recipe.steps.length && recipe.steps [i].step ) {
        // start controller
        context.set ( "step", i + 1 );
        return [
            {   
                topic: "start", 
                recipeName: recipe.name,
                brewStart: recipe.brewStart,
                step: recipe.steps [i].step,
                temperature: recipe.steps [i].temperature, 
                heating: recipe.steps [i].heating,
                stirring: recipe.steps [i].stirring,
                cooling: recipe.steps [i].cooling,
                duration: recipe.steps [i].duration, 
                hysteresis: recipe.steps [i].hysteresis,
                alarm: recipe.steps [i].alarm 
            },
            [
                { id: 0, payload: i === 0 ? true : false },
                { id: 1, payload: i === 1 ? true : false },
                { id: 2, payload: i === 2 ? true : false },
                { id: 3, payload: i === 3 ? true : false },
                { id: 4, payload: i === 4 ? true : false },
                { id: 5, payload: i === 5 ? true : false },
                { id: 6, payload: i === 6 ? true : false },
                { id: 7, payload: i === 7 ? true : false },
                { id: 8, payload: i === 8 ? true : false },
                { id: 9, payload: i === 9 ? true : false },
                { id: 10, payload: i === 10 ? true : false },
                { id: 11, payload: i === 11 ? true : false },
                { id: 12, payload: i === 12 ? true : false }
            ]
        ];
    }
    else {
        // stop controller
        context.set ( "step", null );
        return getStopMessages ( "finished" );
    }

}

if ( msg.topic == "recipe" ) {
    context.set ( "recipe", msg.payload );
    return getStopMessages ( "recipe", msg.payload.name );
}
else if ( msg.topic == "start" ) {
    context.set ( "step", 0 );
    return getStepMessages ( 0 );
}
else if ( msg.topic == "stop" ) {
    context.set ( "step", null );
    return getStopMessages ();
}
else if ( msg.topic == "next" ) {
    var step = context.get ( "step" );
    if ( step ) return getStepMessages ( step );
}

// junand 15.04.2018

if ( msg.topic == "recipe" ) {
    flow.set ( "lastchoice", msg.payload );
    return [null, null];
}

var basepath = global.get ( "basepath" ) + "/recipes/";

var l1 = "recipe_".length;
var l2 = ".json".length;

// 1) create array of files
var s = msg.payload;
var files = s.substring ( 0, s.length - 1 ).split ( "\n" );
//node.warn ( "files=" + files );

// 2) dropdown list
var l = [];
for ( var i = 0; i < files.length; i++ ) {
    var f = files [i];
    if ( f.startsWith ( "recipe_" ) && f.endsWith ( ".json" ) ) {
        var option = f.substring ( l1, f.length - l2 );
        //node.warn ( "option=" + option );
        l [i] = {};
        l[i] [option] = basepath + f;
    }
}

if ( msg.topic == "refresh" ) {
    return [{ topic: "recipe", payload: flow.get ( "lastchoice" ) }, { payload: flow.get ( "lastchoice" ), options: l }];
}
else {
    return [null, { options: l }];
}

// junand 15.04.2018

var result = [];
for ( var i = 0; i < 13; i++ ) {
    result [i] = { id: i, payload: "" };
}

if ( msg.payload.steps ) {
    var l = msg.payload.steps;
    for ( var i = 0; i < l.length; i++ ) {
        if ( l [i].step ) {
            line = "[" + l [i].id + "] " + l [i].step;
            if ( l [i].temperature ) line += ": " + l [i].temperature + "°C";
            if ( l [i].duration === "wait" ) line += ", dann WEITER";
            else line += " für " + l [i].duration + "min";
            if ( l [i].stirring == "full" ) line += " **";
            else if ( l [i].stirring == "control" ) line += " *";
            if ( l [i].cooling && l [i].cooling == "full" ) line += " ~";
            result [i] = { id: i, payload: line };
        }
    }
}

return [result];

if ( msg.payload.temperature1 >= 78.0 ) {
    return [{ payload: "Kessel 1 hat Nachguss-Temperatur erreicht!" }, { payload: "ring" }];
}

if ( msg.payload.temperature2 >= 78.0 ) {
    return [{ payload: "Kessel 2 hat Nachguss-Temperatur erreicht!" }, { payload: "ring" }];
}

return null;

node.status ( 
    { 
        fill: "blue", 
        shape: "dot", 
        text: msg.payload 
    } 
);

return null;
